SELECT * 
FROM ( SELECT /*+INDEX_DESC(book_author SYS_C007573) */ rownum as rn, author_id, author_name, nation_id, regdate, updatedate 
	   FROM	book_author
	   WHERE


			author_name LIKE '%' || #{keyword} || '%' AND


	   rownum <= #{pageNum} * #{amount}
	 )
WHERE rn > (#{pageNum} - 1) * #{amount}
